"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/registry/mini-app/ui/avatar";
import { useProfile } from "@/registry/mini-app/hooks/use-profile";
import { cn } from "@/lib/utils";

type UserAvatarProps = {
  src?: string;
  fallback?: string;
  size?: "xs" | "sm" | "md" | "lg" | "xl" | "2xl" | number;
  shape?: "circle" | "square" | "rounded";
  className?: string;
  useProfileData?: boolean;
  fallbackClassName?: string;
  clickable?: boolean;
  onClickOverride?: () => void;
};

export function UserAvatar({
  src,
  fallback,
  size = "md",
  shape = "circle",
  className = "",
  useProfileData = false,
  fallbackClassName = "",
  clickable = false,
  onClickOverride,
}: UserAvatarProps) {
  const profile = useProfile();

  // Use profile data if requested and available
  const avatarSrc = useProfileData && profile.pfpUrl ? profile.pfpUrl : src;

  // Generate fallback text from profile or use provided fallback
  const getFallbackText = () => {
    if (fallback) return fallback;
    if (useProfileData && profile.displayName) {
      return profile.displayName.substring(0, 2).toUpperCase();
    }
    if (useProfileData && profile.username) {
      return profile.username.substring(0, 2).toUpperCase();
    }
    return "FC";
  };

  // Size classes for predefined sizes
  const sizeClasses = {
    xs: "h-6 w-6 text-xs",
    sm: "h-8 w-8 text-sm",
    md: "h-10 w-10 text-base",
    lg: "h-12 w-12 text-lg",
    xl: "h-16 w-16 text-xl",
    "2xl": "h-20 w-20 text-2xl",
  };

  // Shape classes
  const shapeClasses = {
    circle: "rounded-full",
    square: "rounded-none",
    rounded: "rounded-lg",
  };

  // Custom size if number is provided
  const customSizeStyle = typeof size === 'number' ? { 
    width: `${size}px`, 
    height: `${size}px`,
    fontSize: `${Math.max(size / 3, 12)}px`
  } : {};

  const handleClick = () => {
    if (onClickOverride) {
      onClickOverride();
    } else if (clickable && useProfileData && profile.fid) {
      profile.viewProfile(profile.fid);
    }
  };

  return (
    <Avatar 
      className={cn(
        typeof size === 'string' ? sizeClasses[size] : "",
        shapeClasses[shape],
        "border-0 ring-0 outline-none", // Selalu hapus border
        clickable && (useProfileData && profile.fid) ? "cursor-pointer hover:opacity-80" : "",
        className
      )}
      style={customSizeStyle}
      onClick={clickable || onClickOverride ? handleClick : undefined}
    >
      <AvatarImage src={avatarSrc} alt="User Avatar" className="object-cover" />
      <AvatarFallback className={cn(
        shapeClasses[shape],
        fallbackClassName
      )}>
        {getFallbackText()}
      </AvatarFallback>
    </Avatar>
  );
}

"use client"

import * as React from "react"
import * as AvatarPrimitive from "@radix-ui/react-avatar"

import { cn } from "@/lib/utils"

function Avatar({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Root>) {
  return (
    <AvatarPrimitive.Root
      data-slot="avatar"
      className={cn(
        "relative flex size-8 shrink-0 overflow-hidden rounded-full",
        className
      )}
      {...props}
    />
  )
}

function AvatarImage({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Image>) {
  return (
    <AvatarPrimitive.Image
      data-slot="avatar-image"
      className={cn("aspect-square size-full", className)}
      {...props}
    />
  )
}

function AvatarFallback({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Fallback>) {
  return (
    <AvatarPrimitive.Fallback
      data-slot="avatar-fallback"
      className={cn(
        "bg-muted flex size-full items-center justify-center rounded-full",
        className
      )}
      {...props}
    />
  )
}

export { Avatar, AvatarImage, AvatarFallback }

"use client";

import { useMiniAppSdk } from "./use-miniapp-sdk";
import { useEffect, useState, useCallback } from "react";

export type ProfileData = {
  fid?: number;
  username?: string;
  displayName?: string;
  pfpUrl?: string;
};

/**
 * Hook to access the user profile information from the Farcaster SDK context
 * @returns ProfileData object containing user information and functions to interact with profiles
 */
export function useProfile() {
  const { context, isSDKLoaded, sdk } = useMiniAppSdk();
  const [profile, setProfile] = useState<ProfileData>({});

  useEffect(() => {
    if (isSDKLoaded && context?.user) {
      setProfile({
        fid: context.user.fid,
        username: context.user.username,
        displayName: context.user.displayName,
        pfpUrl: context.user.pfpUrl,
      });
    }
  }, [context, isSDKLoaded]);

  /**
   * View a user's Farcaster profile
   * @param fid - Farcaster ID of the user whose profile to view
   */
  const viewProfile = useCallback(async (fid?: number) => {
    if (!fid) {
      console.warn("No FID provided to viewProfile");
      return;
    }

    try {
      await sdk.actions.viewProfile({ fid });
    } catch (error) {
      console.error("Error viewing profile:", error);
    }
  }, [sdk]);

  /**
   * View the current user's profile or another user's profile if fid is provided
   */
  const viewCurrentOrSpecificProfile = useCallback((specificFid?: number) => {
    const fidToView = specificFid || profile.fid;
    if (fidToView) {
      viewProfile(fidToView);
    }
  }, [profile.fid, viewProfile]);

  return {
    ...profile,
    viewProfile,
    viewCurrentOrSpecificProfile
  };
}
