"use client";

import { DaimoPayButton } from "@daimo/pay";
import { baseUSDC } from "@daimo/contract";
import { getAddress } from "viem";
import { Button } from "@/registry/mini-app/ui/button";

export function DaimoPayTransferButton({
  text,
  toChainId,
  toAddress,
  tokenAddress,
  amount,
  onPaymentStarted,
  onPaymentCompleted,
}: {
  text: string;
  toAddress: `0x${string}`;
  amount: string;
  tokenAddress?: `0x${string}`;
  toChainId?: number;
  onPaymentStarted?: () => void;
  onPaymentCompleted?: () => void;
}) {
  return (
    <div className="flex justify-center text-xl font-bold rounded-lg shadow-lg">
      <DaimoPayButton.Custom
        appId={process.env.NEXT_PUBLIC_DAIMO_PAY_KEY || "pay-demo"}
        toChain={toChainId || baseUSDC.chainId}
        toUnits={amount}
        toToken={tokenAddress || getAddress(baseUSDC.token)}
        toAddress={toAddress}
        onPaymentStarted={(e) => {
          console.log("Payment started", e);
          onPaymentStarted?.();
        }}
        onPaymentCompleted={(e) => {
          console.log("Payment completed", e);
          onPaymentCompleted?.();
        }}
        closeOnSuccess
      >
        {({ show: showDaimoModal }) => (
          <Button className="w-full" size="lg" onClick={() => showDaimoModal()}>
            {text}
          </Button>
        )}
      </DaimoPayButton.Custom>
    </div>
  );
}

"use client";

import { createConfig, http, injected, WagmiProvider } from "wagmi";
import { base, degen, mainnet, optimism } from "wagmi/chains";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { farcasterFrame } from "@farcaster/frame-wagmi-connector";
import { DaimoPayProvider, getDefaultConfig } from "@daimo/pay";

const alchemyApiKey = process.env.NEXT_PUBLIC_ALCHEMY_KEY;

export const config = createConfig(
  getDefaultConfig({
    appName: "hi",
    chains: [base, degen, mainnet, optimism],
    additionalConnectors: [farcasterFrame(), injected()],
    transports: {
      [base.id]: http(
        alchemyApiKey
          ? `https://base-mainnet.g.alchemy.com/v2/${alchemyApiKey}`
          : undefined,
      ),
    },
  }),
);

const queryClient = new QueryClient();

export default function OnchainProvider({
  children,
}: {
  children: React.ReactNode;
}) {
  return (
    <WagmiProvider config={config}>
      <QueryClientProvider client={queryClient}>
        <DaimoPayProvider>{children}</DaimoPayProvider>
      </QueryClientProvider>
    </WagmiProvider>
  );
}

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow-xs hover:bg-primary/90",
        destructive:
          "bg-destructive text-white shadow-xs hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground shadow-xs hover:bg-secondary/80",
        ghost:
          "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2 has-[>svg]:px-3",
        sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

function Button({
  className,
  variant,
  size,
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean;
  }) {
  const Comp = asChild ? Slot : "button";

  return (
    <Comp
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  );
}

export { Button, buttonVariants };
