"use client";

import { useMiniAppSdk } from "@/registry/mini-app/hooks/use-miniapp-sdk";
import { useEffect, useState, useCallback } from "react";

export type ProfileData = {
  fid?: number;
  username?: string;
  displayName?: string;
  pfpUrl?: string;
};

/**
 * Hook to access the user profile information from the Farcaster SDK context
 * @returns ProfileData object containing user information and functions to interact with profiles
 */
export function useProfile() {
  const { context, isSDKLoaded, sdk } = useMiniAppSdk();
  const [profile, setProfile] = useState<ProfileData>({});

  useEffect(() => {
    if (isSDKLoaded && context?.user) {
      setProfile({
        fid: context.user.fid,
        username: context.user.username,
        displayName: context.user.displayName,
        pfpUrl: context.user.pfpUrl,
      });
    }
  }, [context, isSDKLoaded]);

  /**
   * View a user's Farcaster profile
   * @param fid - Farcaster ID of the user whose profile to view
   */
  const viewProfile = useCallback(async (fid?: number) => {
    if (!fid) {
      console.warn("No FID provided to viewProfile");
      return;
    }

    try {
      await sdk.actions.viewProfile({ fid });
    } catch (error) {
      console.error("Error viewing profile:", error);
    }
  }, [sdk]);

  /**
   * View the current user's profile or another user's profile if fid is provided
   */
  const viewCurrentOrSpecificProfile = useCallback((specificFid?: number) => {
    const fidToView = specificFid || profile.fid;
    if (fidToView) {
      viewProfile(fidToView);
    }
  }, [profile.fid, viewProfile]);

  return {
    ...profile,
    viewProfile,
    viewCurrentOrSpecificProfile
  };
}

"use client";

import { useCallback, useEffect, useState } from "react";
import sdk from "@farcaster/frame-sdk";
import type { Context } from "@farcaster/frame-core";

export function useMiniAppSdk() {
  const [isAuthDialogOpen, setIsAuthDialogOpen] = useState(false);

  const [isSDKLoaded, setIsSDKLoaded] = useState(false);
  const [context, setContext] = useState<Context.FrameContext>();
  const [isMiniAppSaved, setIsMiniAppSaved] = useState(false);
  const [lastEvent, setLastEvent] = useState("");
  const [pinFrameResponse, setPinFrameResponse] = useState("");
  const [isMiniApp, setIsMiniApp] = useState(false);

  useEffect(() => {
    if (!sdk) return;

    sdk.on("frameAdded", ({ notificationDetails }) => {
      setLastEvent(
        `frameAdded${notificationDetails ? ", notifications enabled" : ""}`,
      );
      setIsMiniAppSaved(true);
    });

    sdk.on("frameAddRejected", ({ reason }) => {
      setLastEvent(`frameAddRejected, reason ${reason}`);
    });

    sdk.on("frameRemoved", () => {
      setLastEvent("frameRemoved");
      setIsMiniAppSaved(false);
    });

    sdk.on("notificationsEnabled", ({ notificationDetails }) => {
      setLastEvent("notificationsEnabled");
    });

    sdk.on("notificationsDisabled", () => {
      setLastEvent("notificationsDisabled");
    });

    // CRITICAL TO LOAD MINI APP - DON'T REMOVE
    sdk.actions.ready({});
    setIsSDKLoaded(true);

    // Clean up on unmount
    return () => {
      sdk.removeAllListeners();
    };
  }, []);

  useEffect(() => {
    const updateContext = async () => {
      const frameContext = await sdk.context;
      if (frameContext) {
        setContext(frameContext);
        setIsMiniAppSaved(frameContext.client.added);
      }

      const miniAppStatus = await sdk.isInMiniApp();
      setIsMiniApp(miniAppStatus);
    };

    if (isSDKLoaded) {
      updateContext();
    }
  }, [isSDKLoaded]);

  const pinFrame = useCallback(async () => {
    try {
      const result = await sdk.actions.addFrame();
      console.log("addFrame result", result);
      // @ts-expect-error - result type mixup
      if (result.added) {
        setPinFrameResponse(
          result.notificationDetails
            ? `Added, got notificaton token ${result.notificationDetails.token} and url ${result.notificationDetails.url}`
            : "Added, got no notification details",
        );
      }
    } catch (error) {
      setPinFrameResponse(`Error: ${error}`);
    }
  }, []);

  return {
    context,
    pinFrame,
    pinFrameResponse,
    isMiniAppSaved,
    lastEvent,
    sdk,
    isSDKLoaded,
    isAuthDialogOpen,
    setIsAuthDialogOpen,
    isMiniApp,
  };
}
